# Combien y a-t-il de handle dans cette array ?

def journalist_count(journalists)
  puts "Combien il y a-t-il de handle Twitter dans notre base de données ? Cliquez sur la touche entrer pour initiez la requête."
  initiate = gets.chomp
  puts "> Il y a #{journalists.count} handle Twitter dans notre base de données."
  puts " "
end

# Quel est le handle le plus court de cette liste ?

def small_handle(journalists)
  puts "Quel handle Twitter est le plus court dans notre base de données ? Cliquez sur la touche entrer pour initiez la requête."
  initiate = gets.chomp
  result = journalists.min {|a, b| a.length <=> b.length}
  puts "> L'handle Twitter le plus court est #{result}."
  puts " "
end

# Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)

def journalist_count_5min(journalists)
  puts "Combien il y a-t-il de handle Twitter dans notre base de données contenant 5 caractères ? Cliquez sur la touche entrer pour initiez la requête."
  initiate = gets.chomp
  journalists.to_s
  result = journalists.select {|word| word.length == 6}.length
  puts "> Il y a #{result} handle Twitter dans notre base de données contenant 5 caractères."
  puts " "
end

# Combien commencent par une majuscule (première lettre juste après le @) ?

def journalist_count_maj(journalists) 
  puts "Combien il y a-t-il de handle Twitter dans notre base de données qui commencent par une majuscule ? Cliquez sur la touche entrer pour initiez la requête."
  initiate = gets.chomp
  result = journalists.grep(/@[A-Z]/).length
  puts "> Il y a #{result} handle Twitter dans notre base de données commençant par une majuscule."
  puts " "
end

# Trie la liste de handle par ordre alphabétique.

def journalist_sort(journalists)
  puts "Souhaitez vous trier la base de données par ordre alphabétique ? Cliquez sur la touche entrer pour initiez la requête."
  initiate = gets.chomp
  result = journalists.map(&:downcase).sort
  puts "Base de données triées par ordre alphabétique : #{result}"
  puts " "
end

# Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)

def journalist_sort_by_length(journalists)
  puts "Souhaitez vous trier la base de données par taille de l'handle Twitter ? Cliquez sur la touche entrer pour initiez la requête."
  initiate = gets.chomp
  result = journalists.sort_by {|word| word.length}
  puts "Base de données triées par taille de l'handle Twitter : #{result}"
  puts " "
end

# Quelle est la position dans l'array de la personne @epenser ?

def position(journalists)
  puts "Quelle est la position dans la base de données de l'handle Twitter @epenser ? Cliquez sur la touche entrer pour initiez la requête."
  initiate = gets.chomp
  result = journalists.find_index("@epenser")
  puts "@epenser se trouve en position #{result} dans la base de données."
  puts " "
end

#Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)

def length_frequency(journalists)     
  handle_length_frequency = Hash.new(0)
  for i in 0...journalists.length    
    handle_length_frequency[journalists[i].length] +=  1
  end
  puts "Souhaites tu une répartition des handles de la base de données en fonction de leurs nombre de caractères ? Cliquez sur la touche entrer pour initiez la requête."
  initiate = gets.chomp
  a = 1
  for i in 1..33
    if a == 1 or a == 2 or a == 3 or a == 17 or a == 18 or a == 19 or a == 20 or a == 21  or a == 22 or a == 23 or a == 24 or a == 25 or a == 26 or a == 27 or a == 28 or a == 29 or a == 30 or a == 31 or a == 32
      puts "Il y a #{handle_length_frequency.default} handles Twitter avec #{i} caractères"
    else 
      puts "Il y a #{handle_length_frequency.fetch(a)} handles Twitter avec #{i} caractères"
    end
    a += 1
    i += 1
  end
end  

def perform
  journalist_count(data)
  small_handle(data)
  journalist_count_5min(data)
  journalist_count_maj(data)
  journalist_sort(data)
  journalist_sort_by_length(data)
  position(data)
  length_frequency(data)
end

perform
